mapper.select:
SELECT * FROM vika_asset WHERE file_url IN
#{item}
AND is_deleted = 0

mapper.update:
UPDATE vika_asset
SET file_size = file_size + #{incrFileSize}
WHERE id = #{id} AND is_deleted = 0

UPDATE vika_asset
SET file_size = #{entity.fileSize}, mime_type = #{entity.mimeType}
WHERE id = #{entity.id} AND is_deleted = 0
