mapper.select:
SELECT count(*)
FROM vika_unit_role
WHERE space_id = #{spaceId}
  AND role_name = #{roleName}
  AND is_deleted = 0

SELECT count(*)
FROM vika_unit_role
WHERE id = #{id}
  AND space_id = #{spaceId}
  AND is_deleted = 0

SELECT vur.id as id, vu.id as unitId, vur.role_name as role_name, vur.position as position FROM vika_unit_role vur JOIN vika_unit vu ON vur.id = vu.unit_ref_id WHERE vur.id in
#{id}
AND vur.is_deleted = 0 AND vu.is_deleted = 0

SELECT id, role_name FROM vika_unit_role WHERE id in
#{id}
AND is_deleted = 0

