mapper.select:
SELECT DISTINCT vurm.unit_ref_id
FROM vika_unit_role_member vurm
WHERE role_id = #{roleId}

mapper.delete:
DELETE
FROM vika_unit_role_member
WHERE role_id = #{roleId}
AND unit_ref_id in

    #{unitRefId}

mapper.select:
SELECT role_id
FROM vika_unit_role_member
WHERE unit_ref_id = #{unitRefId}

SELECT vu.id as unit_id, vurm.unit_ref_id as unit_ref_id, vurm.unit_type as unit_type FROM vika_unit_role_member vurm JOIN vika_unit vu ON vurm.unit_ref_id = vu.unit_ref_id AND vu.is_deleted = 0 WHERE vurm.role_id = #{roleId} AND vurm.unit_ref_id IN
#{unitRefId}
ORDER BY vurm.unit_ref_id

SELECT count(*)
FROM vika_unit_role_member
WHERE role_id = #{roleId}
