SELECT vom.id, vu.avatar, vu.nick_name, vom.member_name, vom.email, GROUP_CONCAT(distinct vout.id) as teamIds, GROUP_CONCAT(distinct vout.team_name ORDER BY vodmr.created_at DESC SEPARATOR ' | ') as teams, vom.is_active, vom.is_admin as is_primary, IF(vsmrr.id IS NULL, 0, 1) as is_sub_admin, vom.created_at, vom.updated_at FROM ${tablePrefix}unit_team_member_rel vodmr JOIN ${tablePrefix}unit_member vom on vom.id = vodmr.member_id and vom.is_deleted = 0 LEFT JOIN ${tablePrefix}user vu on vom.user_id = vu.id LEFT JOIN ${tablePrefix}unit_team vod on vod.id = vodmr.team_id and vod.is_deleted = 0 LEFT JOIN ${tablePrefix}unit_team_member_rel voutmr on voutmr.member_id = vodmr.member_id LEFT JOIN ${tablePrefix}unit_team vout on vout.id = voutmr.team_id and vout.parent_id != 0 LEFT JOIN ${tablePrefix}space_member_role_rel vsmrr on vsmrr.member_id = vom.id WHERE vodmr.team_id IN
#{item}
