mapper.select:
SELECT *
FROM vika_invitation
WHERE creator = #{memberId}
AND space_id = #{spaceId}
AND node_id = #{nodeId}
AND is_deleted = 0

mapper.update:
UPDATE vika_invitation
SET status = #{status}
WHERE space_id = #{spaceId}
AND node_id != ''
AND is_deleted = 0

mapper.select:
SELECT *
FROM vika_invitation
WHERE invite_token = #{inviteToken}
AND node_id = #{nodeId}
AND is_deleted = 0
