mapper.select:
SELECT COUNT(*)
FROM ${tablePrefix}space_apply
WHERE space_id = #{spaceId}
  AND created_by = #{createdBy}
  AND status = #{status}

mapper.update:
UPDATE ${tablePrefix}space_apply
SET status     = #{status},
    updated_by = #{updatedBy}
WHERE id = #{applyId}

UPDATE ${tablePrefix}space_apply
SET status = 3, failure_reason = #{reason}
WHERE space_id = #{spaceId} AND status = 0
AND created_by IN

    #{item}
