mapper.update:
UPDATE ${tablePrefix}space
        SET owner = #{memberId}

    , updated_by = #{updatedBy}

WHERE space_id = #{spaceId}

UPDATE ${tablePrefix}space
SET owner = NULL, updated_by = #{updatedBy}
WHERE space_id = #{spaceId}

UPDATE ${tablePrefix}space

    props = JSON_SET(props,

        CONCAT('$.', #{item.key}), #{item.value}

    ),

        updated_by = #{userId}


WHERE space_id = #{spaceId}

UPDATE ${tablePrefix}space
SET pre_deletion_time = #{time}, updated_by = #{updatedBy}
WHERE space_id = #{spaceId}
AND is_deleted = 0
