mapper.select:
SELECT COUNT(*)
FROM ${tablePrefix}api_usage vau
WHERE vau.space_id = #{spaceId} AND vau.id >= #{minId}

SELECT MIN(id) FROM ${tablePrefix}api_usage WHERE
id > #{minId} AND
created_at >= #{startTime}

SELECT JSON_EXTRACT(meta_data, '$.views[*].type') AS types FROM ${tablePrefix}datasheet_meta WHERE dst_id IN
#{item}
AND is_deleted = 0

SELECT COUNT(*)
FROM ${tablePrefix}api_usage
WHERE id > #{id}
AND space_id = #{spaceId}

SELECT SUM(total_count)
FROM ${tablePrefix}api_statistics_daily
WHERE space_id = #{spaceId}
  AND statistics_time BETWEEN #{startTime} AND #{endTime}

SELECT SUM(JSON_LENGTH(meta_data -> '$.views[0].rows[*].recordId')) FROM ${tablePrefix}datasheet_meta WHERE dst_id IN
#{item}
AND is_deleted = 0

SELECT COUNT(*)
FROM ${tablePrefix}api_usage
WHERE id > #{id}
AND space_id = #{spaceId}
AND created_at >= #{startTime}
