mapper.select:
SELECT album_id
FROM vika_template_album_rel
WHERE relate_id = #{relateId}
  AND `type` = #{type}

SELECT relate_id
FROM vika_template_album_rel
WHERE album_id = #{albumId}
  AND `type` = #{type}

mapper.insert:
INSERT INTO vika_template_album_rel(id, album_id, type, relate_id)
        VALUES


        #{item.id}, #{item.albumId}, #{item.type}, #{item.relateId}
