mapper.select:
SELECT *
FROM ${tablePrefix}template_property
WHERE property_code = #{propertyCode}
  AND property_type = #{propertyType}
  AND is_deleted = 0 LIMIT 1

SELECT property_name FROM ${tablePrefix}template_property WHERE property_code IN
#{item}
AND is_deleted = 0

SELECT property_code
FROM ${tablePrefix}template_property
WHERE property_type = #{propertyType}
  AND i18n_name = #{i18nName}
  AND is_deleted = 0

SELECT vtp.id as property_id, vtp.property_name, vtp.property_code, vtp.property_type FROM ${tablePrefix}template_property vtp LEFT JOIN ${tablePrefix}template_property_rel vtpr ON vtp.property_code = vtpr.property_code WHERE vtp.is_deleted = 0 AND vtp.i18n_name = #{lang}
AND vtp.property_type = #{type}
GROUP BY vtp.property_code, vtpr.property_order ORDER BY vtpr.property_order ASC

mapper.update:
UPDATE ${tablePrefix}template_property
SET updated_by = #{updatedBy}
        WHERE property_code IN

    #{item}

UPDATE ${tablePrefix}template_property
SET is_deleted = 1,
    updated_by = #{updatedBy}
        WHERE property_code IN

    #{item}

mapper.delete:
UPDATE ${tablePrefix}template_property SET is_deleted = 1, updated_by = #{updatedBy}
WHERE id IN

    #{item}

mapper.select:
SELECT id
from ${tablePrefix}template_property
WHERE property_code = #{code}
  AND property_type = #{type}
  AND is_deleted = 0

SELECT vtp.id as property_id, vtp.property_name, vtp.property_code, vtp.property_type, vtpr.template_id FROM ${tablePrefix}template_property vtp LEFT JOIN ${tablePrefix}template_property_rel vtpr ON vtp.property_code = vtpr.property_code WHERE vtpr.template_id IN
#{item}
AND vtp.property_type = #{type} AND vtp.is_deleted = 0 ORDER BY vtpr.property_order ASC

select count(*)
from ${tablePrefix}template_property
where i18n_name = #{lang}
  and is_deleted = 0;
