mapper.select:
SELECT * FROM vika_user_history vuh
WHERE vuh.user_id = #{userId}
AND vuh.user_status = #{userStatus}
ORDER BY vuh.created_at DESC
LIMIT 1

mapper.update:
UPDATE vika_user_history vuh
SET vuh.updated_by = #{updateUser}
WHERE vuh.user_Id = #{userId}

mapper.select:
SELECT vuh.user_id, vuh.nick_name, vuh.created_at FROM vika_user_history vuh
WHERE
vuh.created_at between #{createdAtBefore} AND #{createdAtAfter}
AND vuh.user_status = #{userStatus}
