mapper.update:
UPDATE ${tablePrefix}widget_package
SET installed_num = installed_num + #{times}
WHERE package_id = #{widgetPackageId}

UPDATE ${tablePrefix}widget_package

    status = #{status}

        ,release_id = #{releaseId}

    ,updated_by = #{userId}

WHERE package_id = #{packageId}
AND is_deleted = 0

mapper.select:
SELECT vwp.package_id, IF(JSON_CONTAINS_PATH(vwp.i18n_name, 'one', '$."${language}"'), vwp.i18n_name ->> '$."${language}"', vwp.i18n_name ->> '$."en-US"') AS name, vwp.icon, vwp.cover, IF(JSON_CONTAINS_PATH(vwp.i18n_description, 'one', '$."${language}"'), vwp.i18n_description ->> '$."${language}"', vwp.i18n_description ->> '$."en-US"') AS description, vwp.status, vwpr.version, vwp.author_name, vwp.author_email, vwp.author_icon, vwp.author_link, vwp.package_type, vwp.release_type FROM ${tablePrefix}widget_package vwp LEFT JOIN ${tablePrefix}widget_package_release vwpr ON vwp.release_id = vwpr.id LEFT JOIN ${tablePrefix}widget_package_auth_space vwpas ON vwp.package_id = vwpas.package_id AND vwpas.type = 0 WHERE vwp.is_deleted = 0
AND vwpas.space_id = #{spaceId}
AND vwp.package_id = #{packageId}

SELECT IF(vwp.release_type = 1, IFNULL(MAX(vwpas.widget_sort), 1), IFNULL(MAX(vwpas.widget_sort), 10000)) AS max_sort FROM ${tablePrefix}widget_package vwp INNER JOIN ${tablePrefix}widget_package_auth_space vwpas ON vwp.package_id = vwpas.package_id AND vwpas.is_deleted = 0 WHERE vwp.is_deleted = 0
AND vwp.release_type = 1
AND vwp.release_type = 0 AND vwpas.space_id = #{spaceId}

SELECT COUNT(*)
FROM ${tablePrefix}widget_package vwp
WHERE vwp.widget_body -> '$.issuedIdArchive.dstId' = #{dstId}
AND vwp.widget_body -> '$.issuedIdArchive.recordId' = #{recordId}

SELECT COUNT(*)
FROM ${tablePrefix}widget_package vwp
WHERE vwp.widget_body -> '$.auditSubmitResultArchive.dstId' = #{dstId}
AND vwp.widget_body -> '$.auditSubmitResultArchive.recordId' = #{recotdId}
