#{item}
and node_name LIKE CONCAT('%', #{likeName}, '%') and parent_id != '0' and is_template = 0 ORDER BY (case when node_name = #{likeName} then 1 when node_name like CONCAT(#{likeName}, '%') then 2 when node_name like CONCAT('%', #{likeName}) then 3 when node_name like CONCAT('%', #{likeName}, '%') then 4 else 0 end) limit 0,50

