SELECT n.node_id, n.node_name, n.type, n.icon, IF(n2.parent_id = '0', NULL, n.parent_id) as parent_id, IF(nf.id IS NULL, 0 ,1) as node_favorite FROM ${tablePrefix}node n LEFT JOIN ${tablePrefix}node n2 ON n2.node_id = n.parent_id LEFT JOIN ${tablePrefix}node_favorite nf ON nf.node_id = n.node_id AND nf.member_id = #{memberId} WHERE n.node_id IN
#{item}
AND n.is_rubbish = 0

SELECT vn.node_id, vn.space_id, IF(vn.parent_id = '0', vs.name, vn.node_name) AS node_name, vn.type, vn.extra, vn.parent_id, IF(vn.unit_id = 0, 0, 1) node_private, vn.pre_node_id, vn.icon, IF(leaf.id IS NULL, 0, 1) has_children,vdm.meta_data, vn.created_at, vn.updated_at, IF(vnss.id IS NULL, 0 ,1) AS node_shared, IF(vc.id IS NULL, 0 ,1) as node_permit_set, IF(vnf.id IS NULL, 0 ,1) as node_favorite FROM ${tablePrefix}node vn JOIN ${tablePrefix}space vs on vn.space_id = vs.space_id LEFT JOIN ${tablePrefix}node leaf on leaf.parent_id = vn.node_id and leaf.is_rubbish = 0 LEFT JOIN ${tablePrefix}node_favorite vnf ON vnf.node_id = vn.node_id AND vnf.member_id = #{memberId} LEFT JOIN ${tablePrefix}datasheet_meta vdm ON vdm.dst_id = vn.node_id AND vdm.is_deleted = 0 LEFT JOIN ${tablePrefix}node_share_setting vnss on vn.node_id = vnss.node_id AND vnss.is_enabled = 1 LEFT JOIN ${tablePrefix}control vc on vn.node_id = vc.control_id AND vc.is_deleted = 0 WHERE vn.node_id IN
#{item}
AND vn.is_rubbish = 0 AND vn.is_deleted = 0 GROUP BY vn.node_id

SELECT vn.node_id, vn.space_id, IF(vn.parent_id = '0', vs.name, vn.node_name) AS node_name, vn.type, vn.extra, vn.parent_id, IF(vn.unit_id = 0, 0, 1) node_private, vn.pre_node_id, vn.icon, IF(leaf.id IS NULL, 0, 1) has_children, JSON_LENGTH(vdm.meta_data -> '$.fieldMap') AS md_field_map_size, vn.created_at, vn.updated_at, IF(vnss.id IS NULL, 0 ,1) as node_shared, IF(vc.id IS NULL, 0 ,1) AS node_permit_set, IF(vnf.id IS NULL, 0 ,1) AS node_favorite FROM ${tablePrefix}node vn JOIN ${tablePrefix}space vs on vn.space_id = vs.space_id LEFT JOIN ${tablePrefix}node leaf on leaf.parent_id = vn.node_id and leaf.is_rubbish = 0 LEFT JOIN ${tablePrefix}node_favorite vnf ON vnf.node_id = vn.node_id AND vnf.member_id = #{memberId} LEFT JOIN ${tablePrefix}datasheet_meta vdm ON vdm.dst_id = vn.node_id AND vdm.is_deleted = 0 LEFT JOIN ${tablePrefix}node_share_setting vnss on vn.node_id = vnss.node_id AND vnss.is_enabled = 1 LEFT JOIN ${tablePrefix}control vc on vn.node_id = vc.control_id AND vc.is_deleted = 0 WHERE vn.node_id IN
#{item}
AND vn.is_rubbish = 0 GROUP BY vn.node_id

mapper.update:
UPDATE ${tablePrefix}node
SET is_rubbish = #{isRubbish}, updated_by = #{userId}
WHERE node_id IN

    #{item}

AND is_rubbish != #{isRubbish} AND is_deleted = 0

UPDATE ${tablePrefix}node
SET is_deleted = 1, updated_by = #{userId}
WHERE node_id = #{nodeId} AND is_rubbish = 1 AND is_deleted = 0

UPDATE ${tablePrefix}node
SET deleted_path = #{delPath},
    parent_id    = -1,
    is_rubbish   = 1,
    updated_by   = #{userId}
WHERE node_id = #{nodeId}

UPDATE ${tablePrefix}node vn
JOIN ${tablePrefix}node vn2 ON vn.pre_node_id = vn2.node_id
SET vn.pre_node_id = vn2.pre_node_id
WHERE vn.parent_id = #{parentId}
AND vn.pre_node_id = #{preNodeId}
AND vn.is_rubbish = 0

UPDATE ${tablePrefix}node
SET pre_node_id = #{newPreNodeId}
WHERE parent_id = #{parentId}


        AND pre_node_id = #{originPreNodeId}


        AND pre_node_id IS NULL


AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET pre_node_id = #{updatedPreNodeId}
WHERE node_id = #{nodeId}
AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET icon = #{icon}
WHERE node_id = #{nodeId} AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET node_name = #{nodeName}
WHERE node_id = #{nodeId} AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET cover = #{cover}
WHERE node_id = #{nodeId} AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET is_banned = #{status}
WHERE node_id = #{nodeId} AND is_rubbish = 0

