SELECT vn.node_id, vn.space_id, IF(vn.parent_id = '0', vs.name, vn.node_name) AS node_name, vn.type, vn.parent_id, vn.pre_node_id, vn.icon, IF(leaf.id IS NULL, 0, 1) has_children,vdm.meta_data, vn.created_at, vn.updated_at, IF(vnss.id IS NULL, 0 ,1) AS node_shared, IF(vc.id IS NULL, 0 ,1) as node_permit_set, IF(vnf.id IS NULL, 0 ,1) as node_favorite FROM ${tablePrefix}node vn JOIN ${tablePrefix}space vs on vn.space_id = vs.space_id LEFT JOIN ${tablePrefix}node leaf on leaf.parent_id = vn.node_id and leaf.is_rubbish = 0 LEFT JOIN ${tablePrefix}node_favorite vnf ON vnf.node_id = vn.node_id AND vnf.member_id = #{memberId} LEFT JOIN ${tablePrefix}datasheet_meta vdm ON vdm.dst_id = vn.node_id AND vdm.is_deleted = 0 LEFT JOIN ${tablePrefix}node_share_setting vnss on vn.node_id = vnss.node_id AND vnss.is_enabled = 1 LEFT JOIN ${tablePrefix}control vc on vn.node_id = vc.control_id AND vc.is_deleted = 0 WHERE vn.node_id IN
#{item}
AND vn.is_rubbish = 0 GROUP BY vn.node_id

SELECT vn.node_id, vn.space_id, IF(vn.parent_id = '0', vs.name, vn.node_name) AS node_name, vn.type, vn.parent_id, vn.pre_node_id, vn.icon, IF(leaf.id IS NULL, 0, 1) has_children, JSON_LENGTH(vdm.meta_data -> '$.fieldMap') AS md_field_map_size, vn.created_at, vn.updated_at, IF(vnss.id IS NULL, 0 ,1) as node_shared, IF(vc.id IS NULL, 0 ,1) AS node_permit_set, IF(vnf.id IS NULL, 0 ,1) AS node_favorite FROM ${tablePrefix}node vn JOIN ${tablePrefix}space vs on vn.space_id = vs.space_id LEFT JOIN ${tablePrefix}node leaf on leaf.parent_id = vn.node_id and leaf.is_rubbish = 0 LEFT JOIN ${tablePrefix}node_favorite vnf ON vnf.node_id = vn.node_id AND vnf.member_id = #{memberId} LEFT JOIN ${tablePrefix}datasheet_meta vdm ON vdm.dst_id = vn.node_id AND vdm.is_deleted = 0 LEFT JOIN ${tablePrefix}node_share_setting vnss on vn.node_id = vnss.node_id AND vnss.is_enabled = 1 LEFT JOIN ${tablePrefix}control vc on vn.node_id = vc.control_id AND vc.is_deleted = 0 WHERE vn.node_id IN
#{item}
AND vn.is_rubbish = 0 GROUP BY vn.node_id

WITH RECURSIVE tree_view(node_id) AS ( SELECT vn.node_id FROM ${tablePrefix}node vn WHERE vn.space_id = #{spaceId} AND vn.pre_node_id IS NULL AND vn.is_rubbish = 0 AND vn.parent_id IN ( WITH RECURSIVE tree (node_id, type, lvl) AS ( SELECT node_id, type, 1 lvl FROM ${tablePrefix}node WHERE node_id = #{nodeId} AND is_rubbish = 0 UNION ALL SELECT vn.node_id, vn.type, c.lvl + 1 FROM tree c JOIN ${tablePrefix}node vn ON vn.parent_id = c.node_id AND vn.is_rubbish = 0
WHERE c.lvl < #{depth}
) SELECT node_id FROM tree WHERE type != 2 ) UNION ALL SELECT vn.node_id FROM tree_view p JOIN ${tablePrefix}node vn ON p.node_id = vn.pre_node_id AND vn.is_rubbish = 0 ) SELECT node_id FROM tree_view GROUP BY node_id UNION SELECT #{nodeId} AS node_id

WITH RECURSIVE parent_view (node_id, node_name, parent_id) AS ( SELECT n.node_id, n.node_name, n.parent_id FROM ${tablePrefix}node n WHERE n.space_id = #{spaceId} AND n.node_id IN
#{item}
AND n.is_rubbish = 0 UNION SELECT c.node_id, c.node_name, c.parent_id FROM parent_view AS pv JOIN ${tablePrefix}node AS c ON pv.parent_id = c.node_id AND c.is_rubbish = 0 ) SELECT node_id, node_name, parent_id FROM parent_view WHERE parent_id != '0'

mapper.update:
UPDATE ${tablePrefix}node
SET is_rubbish = #{isRubbish}, updated_by = #{userId}
WHERE node_id IN

    #{item}

AND is_rubbish != #{isRubbish} AND is_deleted = 0

UPDATE ${tablePrefix}node
SET is_deleted = 1, updated_by = #{userId}
WHERE node_id = #{nodeId} AND is_rubbish = 1 AND is_deleted = 0

UPDATE ${tablePrefix}node
SET deleted_path = #{delPath}, parent_id = -1
WHERE node_id = #{nodeId}

UPDATE ${tablePrefix}node vn
JOIN ${tablePrefix}node vn2 ON vn.pre_node_id = vn2.node_id
SET vn.pre_node_id = vn2.pre_node_id
WHERE vn.parent_id = #{parentId}
AND vn.pre_node_id = #{preNodeId}
AND vn.is_rubbish = 0

UPDATE ${tablePrefix}node
SET pre_node_id = #{newPreNodeId}
WHERE parent_id = #{parentId}


        AND pre_node_id = #{originPreNodeId}


        AND pre_node_id IS NULL


AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET icon = #{icon}
WHERE node_id = #{nodeId} AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET node_name = #{nodeName}
WHERE node_id = #{nodeId} AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET cover = #{cover}
WHERE node_id = #{nodeId} AND is_rubbish = 0

UPDATE ${tablePrefix}node
SET is_banned = #{status}
WHERE node_id = #{nodeId} AND is_rubbish = 0

mapper.select:
SELECT type
FROM ${tablePrefix}node
WHERE node_id = #{nodeId}

