#{item}
AND is_rubbish = 0

SELECT space_id
FROM ${tablePrefix}node
WHERE node_id = #{nodeId}

SELECT DISTINCT is_template FROM ${tablePrefix}node WHERE node_id IN
#{item}
AND is_rubbish = 0

SELECT node_id
FROM ${tablePrefix}node
WHERE space_id = #{spaceId} AND parent_id = '-1' AND is_rubbish = 1 AND is_deleted = 0

    AND updated_at > #{beginTime}


    AND updated_at < #{endTime}

ORDER BY updated_at DESC
LIMIT #{size}

SELECT vn.node_id, vn.space_id, vn.node_name, vn.type, vn.icon, vn.updated_at AS deletedAt, vn.deleted_path AS delPath, vu.uuid, vu.avatar, vu.color AS avatarColor, vu.nick_name AS nickName, vum.member_name, #{retainDay} AS retainDay, IFNULL(vu.is_social_name_modified, 2) > 0 AS isNickNameModified, IFNULL(vum.is_social_name_modified, 2) > 0 AS isMemberNameModified FROM ${tablePrefix}node vn LEFT JOIN ${tablePrefix}user vu ON vn.updated_by = vu.id AND vu.is_deleted = 0 LEFT JOIN ${tablePrefix}unit_member vum ON vu.id = vum.user_id AND vum.space_id = #{spaceId} AND vum.is_deleted = 0 WHERE vn.node_id IN
#{item}
AND vn.parent_id = '-1' AND vn.is_rubbish = 1 AND vn.is_deleted = 0 ORDER BY vn.updated_at DESC

