mapper.select:
SELECT id
FROM vika_api_usage
WHERE id > #{id}
  AND DATE_FORMAT(created_at, '%Y-%m') = #{monthTime}
LIMIT 1

SELECT DATE_FORMAT(vau.created_at, '%Y-%m-%d') AS statistics_time, vau.space_id, COUNT(*) AS total_count, IFNULL(SUM(JSON_EXTRACT(vau.res_detail, '$.code') = 200), 0) AS success_count FROM vika_api_usage vau WHERE vau.id >= #{beginId}
AND vau.id < #{endId}
AND TO_DAYS(NOW()) - TO_DAYS(vau.created_at) >= 1
GROUP BY statistics_time, vau.space_id

mapper.insert:
INSERT INTO vika_api_statistics_daily(id, space_id, statistics_time, total_count, success_count, failure_count, created_at)
VALUE


        #{item.id},#{item.spaceId},#{item.statisticsTime},#{item.totalCount},#{item.successCount},#{item.failureCount},#{item.createdAt}

INSERT INTO vika_api_statistics_monthly(id, space_id, statistics_time, total_count, success_count, failure_count, created_at)
VALUE


        #{item.id},#{item.spaceId},#{item.statisticsTime},#{item.totalCount},#{item.successCount},#{item.failureCount},#{item.createdAt}
