  "player_step_ui_config_32": "{\n \"element\": \"#DATASHEET_GRID_CUR_COLUMN_TYPE\", \n\"placement\": \"bottom\",\n \"title\": \"How to use a datasheet\", \n\"description\": \"Vikadata provides various types of fields. Hover on one field type to view the details.\", \"children\":\"\" \n}",
  "player_step_ui_config_33": "{\n \"element\": \"#DATASHEET_VIEW_TAB_BAR .style_viewBarWrapper__AJlc-\", \n\"placement\": \"bottom\",\n \"title\": \"How to use a datasheet\", \n\"description\": \"A datasheet provides a variety of views, in which grouping, filtering, sorting and other features help you customize the data layout. All views of a datasheet use the same data source, so do not use the view as an Excel sheet!\", \"children\":\"\" \n}",
  "player_step_ui_config_34": "{\n \"element\": \"#DATASHEET_VIEW_TAB_BAR .style_viewBarWrapper__AJlc-\"\n}",
  "player_step_ui_config_35": "{\n \"element\": \"#DATASHEET_ADD_VIEW_BTN\", \n\"placement\": \"bottom\",\n \"title\": \"How to use a datasheet\", \n\"description\": \"Vikadata supports a variety of views such as Grid, Gallery, Kanban, Gantt, Calendar, and Architecture. Different views correspond to different data visualizations and workflows. Again, views are only displayed differently, but they come from the same data source!\", \"children\":\"\" \n}",
