  "player_step_ui_config_50": "{\n \"element\": \".style_widgetModal__eXmdB\",\n\"placement\": \"leftTop\",\n \"title\": \"Widget Center\", \n\"description\": \"Officially recommended and custom widgets are published here. You can install any widget you want onto a dashboard or into a widget board of any datasheet.\", \"children\":\"\" \n}",
