  "player_step_ui_config_59": "{\n \"element\": \"#DASHBOARD_PANEL_ID .style_widgetContainer__2HwEf\",\n\"placement\": \"rightTop\",\n \"title\": \"Link to datasheet\", \n\"description\": \"Some widgets need a data source, so you need to select a datasheet to link to after adding a widget\", \"children\":\"\" \n}",
