  "player_step_ui_config_70": "{\n \"element\": \"div[data-guide-id=WIDGET_ITEM_WRAPPER]:last-child [data-guide-id=WIDGET_ITEM_MORE]\",\n\"placement\": \"topRight\",\n \"title\": \"Exit dev mode\", \n\"description\": \"Click here to exit dev mode, which breaks the connection to the local service and resumes to the latest published widget version\", \"children\":\"\"\n}",
