  "player_step_ui_config_52": "{\n \"element\": \".style_widgetModal__eXmdB .style_widgetItem__3Pl-1 button\",\n\"shadowDirection\":\"none\"\n}",
  "player_step_ui_config_53": "{\n \"element\": \".style_widgetPanelContainer__1l2ZV\",\n\"placement\": \"leftCenter\",\n\"description\": \"安装成功，同时我们也为你创建了一个小程序面板，一个维格表的所有小程序都会放置在这个区域\", \"children\":\"\" \n}",
  "player_step_ui_config_54": "{\n \"element\": \".style_widgetPanelContainer__1l2ZV .style_panelHeader__3X0pG\",\n\"placement\": \"bottom\",\n \"title\": \"小程序面板\", \n\"description\": \"小程序面板是用于装载小程序的容器，可以通过创建多个小程序面板来给你的小程序进行分类\", \"children\":\"\" \n}",
  "player_step_ui_config_55": "{\n \"placement\": \"bottomLeft\",\n  \"description\": \"仪表盘作为维格表的一种文件类型，添加/导入小程序后，可视化能力提升。你可以通过数据统计和图表分析，更好地进行判断和决策\"\n}",
  "player_step_ui_config_56": "{\n \"element\": \"#DASHBOARD_PANEL_ID .style_tabRight__4YAkM button:nth-of-type(1)\",\n\"placement\": \"bottom\",\n \"title\": \"添加小程序\", \n\"description\": \"在这里有两种方法添加小程序，一种是从小程序中心添加，一种是从已有的小程序中导入进去\", \"children\":\"\" \n}",
