  "player_step_ui_config_69": "{\n \"element\": \"div[data-guide-id=WIDGET_ITEM_WRAPPER]:last-child [data-guide-id=WIDGET_ITEM_REFRESH]\",\n\"placement\": \"topRight\",\n \"title\": \"刷新\", \n\"description\": \"每次本地改动代码后，都需要点击刷新来预览\", \"children\":\"\"\n}",
